scripts.build:
echo "export let BRANCH_NAME = \"`git rev-parse --abbrev-ref HEAD`\";
export let COMMIT_HASH = \"`git rev-parse --short HEAD`\";
export let BUILD_DATE = \"`date --utc`\";
export let COMMIT_MESSAGE = \"`git log --format=%B -n 1 HEAD`\";" > ./src/version.ts; webpack --env=dev

scripts.dist:
echo "export let BRANCH_NAME = \"`git rev-parse --abbrev-ref HEAD`\";
export let COMMIT_HASH = \"`git rev-parse --short HEAD`\";
export let BUILD_DATE = \"`date --utc`\";
export let COMMIT_MESSAGE = \"`git log --format=%B -n 1 HEAD`\";" > ./src/version.ts; webpack --env=prod
